/**
 * Get detailed information for a specific checklist item
 * @param {number} sectionId - The section ID (1, 2, or 3)
 * @param {string} categoryTitle - The category title
 * @param {string} itemTitle - The specific item title
 * @returns {Object|null} The item details or null if not found
 */
export const getItemDetails = (sectionId, categoryTitle, itemTitle) => {
  const sectionKey = `section-${sectionId}`;
  
  // First, check if it's a direct item in the category
  if (checklistDatabase[sectionKey] && 
      checklistDatabase[sectionKey][categoryTitle] && 
      checklistDatabase[sectionKey][categoryTitle][itemTitle]) {
    return checklistDatabase[sectionKey][categoryTitle][itemTitle];
  }
  
  // If not found, check if it might be in a nested structure
  if (checklistDatabase[sectionKey] && checklistDatabase[sectionKey][categoryTitle]) {
    const category = checklistDatabase[sectionKey][categoryTitle];
    
    // Look through all properties of the category to find nested items
    for (const nestedKey in category) {
      // If the property is an object (potential dropdown)
      if (typeof category[nestedKey] === 'object' && category[nestedKey] !== null) {
        // Check if the nested item has the itemTitle as a subkey
        if (category[nestedKey][itemTitle]) {
          return category[nestedKey][itemTitle];
        }
      }
    }
  }
  
  // Return default information if specific item not found
  return {
    guidelines: `When ${itemTitle.toLowerCase()}, it's recommended to consult professionals and take your time with decisions. This task is an important step in the process.`
  };
};

export default checklistDatabase; 